        function addToCart(name, price) {
            let priceNumber = parseFloat(price.slice(1));
            if (itemsInCart === 0) $('#cart').text("" "");
            let newDiv = $('<div class=""cart-item""></div>');
            newDiv.text(name + ' ... ' + price + ' ');
