            newDiv.append('<hr>');
            newDiv.attr('name', name);
            newDiv.attr('price', priceNumber);
            $('#cart').append(newDiv);
            newDiv.animateCss('bounceInRight');
            itemsInCart++;
            $('#cartItems').text(itemsInCart);
            subTotal += priceNumber;
            updatePrice();
        }

        function deleteItem(e) {
            let price = $(e.parentElement).attr('price');
            subTotal -= price;
